from browser import document, window


########################################################################
class Tools:
    """"""


    #----------------------------------------------------------------------
    @classmethod
    def bind(self, selector, event, fn):
        """"""
        return [element.bind(event, fn) for element in document.get(selector=selector)]


    #----------------------------------------------------------------------
    @classmethod
    def s(self, selector):
        """"""
        e = document.get(selector=selector)
        if len(e) == 1:
            e = e[0]

        return e


    #----------------------------------------------------------------------
    @classmethod
    def jq(self, selector):
        """"""
        return window.jQuery(selector)




